<?phprequire_once "../controllers/annualreturns.controller.php";require_once "../models/annualreturns.model.php";class AjaxAnnuals{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditAnnuals(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = ControllerAnnuals::ctrShowAnnuals($item, $value);		echo json_encode($answer);	}	/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = ControllerAnnuals::ctrShowAnnuals($item, $value);		echo json_encode($answer);	}}/*=============================================EDIT CUSTOMER=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxAnnuals();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditAnnuals();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxAnnuals();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}