<?phprequire_once "../controllers/cidb.controller.php";require_once "../models/cidb.model.php";class AjaxEmpcidb{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpcidb(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controllerempcidb::ctrShowempcidb($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controllerempcidb::ctrShowempcidb($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Empcidb=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpcidb();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpcidb();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpcidb();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}