<?phprequire_once "../controllers/coida.controller.php";require_once "../models/coida.model.php";class AjaxEmpcoida{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpcoida(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controllerempcoida::ctrShowempcoida($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controllerempcoida::ctrShowempcoida($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Empcoida=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpcoida();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpcoida();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpcoida();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}