<?phprequire_once "../controllers/emp201.controller.php";require_once "../models/emp201.model.php";class AjaxEmp201{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmp201(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controlleremp201::ctrShowemp201($item, $value);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controlleremp201::ctrShowemp201($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Emp201=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmp201();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmp201();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmp201();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}