<?phprequire_once "../controllers/irp6.controller.php";require_once "../models/irp6.model.php";class AjaxEmpirp6{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpirp6(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controllerempirp6::ctrShowempirp6($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controllerempirp6::ctrShowempirp6($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Empirp6=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpirp6();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpirp6();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpirp6();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}