<?phprequire_once "../controllers/vatA.controller.php";require_once "../models/vatA.model.php";class AjaxEmpvatA{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpvatA(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = ControllerempvatA::ctrShowempvatA($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = ControllerempvatA::ctrShowempvatA($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT EmpvatA=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpvatA();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpvatA();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpvatA();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}