<?phprequire_once "../controllers/vatC.controller.php";require_once "../models/vatC.model.php";class AjaxEmpvatC{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpvatC(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = ControllerempvatC::ctrShowempvatC($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = ControllerempvatC::ctrShowempvatC($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT EmpvatC=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpvatC();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpvatC();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpvatC();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}