<?phprequire_once "../controllers/objvat.controller.php";require_once "../models/objvat.model.php";class AjaxEmpobjvat{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmpobjvat(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controllerempobjvat::ctrShowempobjvat($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controllerempobjvat::ctrShowempobjvat($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Empobjvat=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmpobjvat();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmpobjvat();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmpobjvat();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}