<?phprequire_once "../controllers/taxclearance.controller.php";require_once "../models/taxclearance.model.php";class AjaxEmptaxclearance{	/*=============================================	EDIT CUSTOMER	=============================================*/		public $regNumber;	public function ajaxEditEmptaxclearance(){		$item = "REGISTRATION_NUMBER";		$value = $this->regNumber;		$answer = Controlleremptaxclearance::ctrShowemptaxclearance($item, $value);		//print_r($answer);					echo json_encode($answer);	}				/*=============================================	VALIDATE IF USER ALREADY EXISTS	=============================================*/	public $validateregNumber;	public function ajaxValidateRegNumber(){		$item = "REGISTRATION_NUMBER";		$value = $this->validateregNumber;		$answer = Controlleremptaxclearance::ctrShowemptaxclearance($item, $value);		//print_r($answer);		echo json_encode($answer);	}}/*=============================================EDIT Emptaxclearance=============================================*/	if(isset($_POST["regNumber"])){	$Customer = new AjaxEmptaxclearance();	$Customer -> regNumber = $_POST["regNumber"];	$Customer -> ajaxEditEmptaxclearance();}/*=============================================VALIDATE IF USER ALREADY EXISTS=============================================*/if (isset($_POST["validateregNumber"])) {	$valUser = new AjaxEmptaxclearance();	$valUser -> validateregNumber = $_POST["validateregNumber"];	$valUser -> ajaxValidateRegNumber();}